const regex = require('../methods/regex');
module.exports = {
    durations: {
        millisecond: regex('(\d+)?m(?:s?)(?:illisekunde(?:n?)?)', 'ig'),
        second: regex('(\d+)?s[^t](?:ekunde(?:n?)?)', 'ig'),
        minute: regex('(\d+)?m[^SO](?:in(?:ute(?:n?)?)?)', 'ig'),
        hour: regex('(\\d+)[^a-z0-9]*?st(?:unde(?:n?)?)?', 'ig'),
        day: regex('(\\d+)[^a-z0-9]*?t(?:ag(?:e?)?)?', 'ig'),
        week: regex('(\\d+)[^a-z0-9]*?w(?:och(?:e?)?)?', 'ig'),
        month: regex('(\\d+)[^a-z0-9]*?mo(?:nat(?:e?)?)?', 'ig'),
        year: regex('(\\d+)[^a-z0-9]*?j(?:ahr(?:e?)?)?', 'ig')
    },
    constants: new Map([
        ["milliseconds", regex('\{MS\}|\{M(?:S?)(?:ILLISEKUNDE(?:N?)?)\}', 'ig')],
        ["seconds", regex('\{S[^T]\}|\{S(?:EK(?:UNDE(?:N?)?)?)\}', 'ig')],
        ["minutes", regex('\{M[^SO]\}|\{M(?:IN(?:UTE(?:N?)?)?)\}', 'ig')],
        ["hours", regex('\{ST\}|\{st(?:unde(?:n?)?)\}', 'ig')],
        ["days", regex('\{T\}|\{t(?:ag(?:e?)?)\}', 'ig')],
        ["weeks", regex('\{W\}|\{w(?:oche(?:n?)?)\}', 'ig')],
        ["months", regex('\{MO\}|\{mo(?:nat(?:e?)?)?\}', 'ig')],
        ["years", regex('\{J\}|\{J(?:ahr(?:e?)?)\}', 'ig')]
    ])
}