const regex = require('../methods/regex');
module.exports = {
    durations: {
        millisecond: regex('(\\d+)[^a-z0-9]*?ms', 'ig'),
        second: regex('(\\d+)[^a-z0-9]*?s(?:ec?)?', 'ig'),
        minute: regex('(\\d+)[^a-z0-9]*?m[^so](?:n?)?', 'ig'),
        hour: regex('(\\d+)[^a-z0-9]*?h(?:our?)?', 'ig'),
        day: regex('(\\d+)[^a-z0-9]*?d(?:ay?)?', 'ig'),
        week: regex('(\\d+)[^a-z0-9]*?w(?:eek?)?', 'ig'),
        month: regex('(\\d+)[^a-z0-9]*?mo(?:onth?)?', 'ig'),
        year: regex('(\\d+)[^a-z0-9]*?y(?:ear?)?', 'ig')
    },
    constants: new Map([
        ["milliseconds", regex('\{MS\}|\{?MI(?:LLISECOND(?:S?)?)\}', 'ig')],
        ["seconds", regex('\{S\}|\{S(?:EC(?:OND(?:S?)?)?)\}', 'ig')],
        ["minutes", regex('\{M[^SO]\}|\{M(?:IN(?:UTE(?:S?)?)?)\}', 'ig')],
        ["hours", regex('\{H\}|\{H(?:OUR(?:S?)?)\}', 'ig')],
        ["days", regex('\{D\}|\{D(?:AY(?:S?)?)\}', 'ig')],
        ["weeks", regex('\{W\}|\{W(?:EEK(?:S?)?)\}', 'ig')],
        ["months", regex('\{MO\}|\{MO(?:NTH(?:S?)?)\}', 'ig')],
        ["years", regex('\{Y\}|\{Y(?:EAR(?:S?)?)\}', 'ig')]
    ])
}