const TimeConstruct = require('../classes/TimeConstruct');
const match = require('./match');
const locales = require('../locales/index');
module.exports = (time, config={balance:true}, locale) => {
    let operations = locales[locale].durations;
    let milliseconds = match(operations.millisecond, time);
    let seconds = match(operations.second, time);
    let minutes = match(operations.minute, time);
    let hours = match(operations.hour, time);
    let days = match(operations.day, time);
    let weeks = match(operations.week, time);
    let months = match(operations.month, time);
    let years = match(operations.year, time);
    return new TimeConstruct(milliseconds, seconds, minutes, hours, days, weeks, months, years, time, {balance: config.balance, locale});
}